#include <iostream>

struct Node {
  int data;
  Node* left;
  Node* right;

  Node(int value) : data(value), left(nullptr), right(nullptr) {}
};

struct BST {
  Node* top = nullptr;

  Node* insert(Node* root, int value) {
    if (root == nullptr) {
        return new Node(value);
    }
    if (value < root->data) {
      root->left = insert(root->left, value);
    } else if (value > root->data) {
      root->right = insert(root->right, value);
    }
    return root;
  }

  Node* findMin(Node* root) {
    while (root && root->left != nullptr) {
      root = root->left;
    }
    return root;
  }

  Node* pop(Node* root, int value) {
    if (root == nullptr) return nullptr;

    if (value < root->data) {
      root->left = pop(root->left, value);
    } else if (value > root->data) {
      root->right = pop(root->right, value);
    } else {
      if (root->left == nullptr) {
        Node* temp = root->right;
        delete root;
        return temp;
      } else if (root->right == nullptr) {
        Node* temp = root->left;
        delete root;
        return temp;
      }
      Node* temp = findMin(root->right);
      root->data = temp->data;
      root->right = pop(root->right, temp->data);
    }
    return root;
  }

  void inorder(Node* root) {
    if (root == nullptr) return;

    inorder(root->left);
    std::cout << root->data << " ";
    inorder(root->right);
  }

  void clear(Node* root) {
    if (root == nullptr) return;

    clear(root->left);
    clear(root->right);
    delete root;
  }

  void merge(BST& other) {
    merge(top, other.top);
    clear(other.top);
    other.top = nullptr;
  }

  void merge(Node*& root1, Node* root2) {
    if (root2 == nullptr) return;

    merge(root1, root2->left);
    root1 = insert(root1, root2->data);
    merge(root1, root2->right);
  }
};


int main() {
  int n;
  std::cin >> n;
  std::string cmd;
  int acc;
  int id;
  BST arr[2];

  for (int i = 0; i < n; ++i) {
    std::cin >> cmd;
    if (cmd == "merge") {
      arr[0].merge(arr[1]);
      arr[0].inorder(arr[0].top);
      std::cout << '\n';
    } else if (cmd == "buy") {
      std::cin >> acc >> id;
      arr[acc - 1].top = arr[acc - 1].insert(arr[acc - 1].top, id);
    } else if (cmd == "sell") {
      std::cin >> acc >> id;
      arr[acc - 1].top = arr[acc - 1].pop(arr[acc - 1].top, id);
    }
  }
}


