public class HumidityControl extends Thread {

	private volatile boolean running = true;
	private volatile boolean pauseSimulation;

	private JTextField IdealHumidityText;
	private JTextField IdealHumidityRangeText;
	private JTextField HumidifierOnRateText;
	private JTextField HumidifierOffRateText;
	private JLabel CurrentHumidityLabel;
	private JPanel HumidifierIndicator;
	private int CurrentHumidity;
	private HumidifierMode humidifierMode;
	public StringBuilder SimulationData = new StringBuilder();


	public HumidityControl(JTextField IdealHumidityText, JTextField IdealHumidityRangeText, 
			JTextField HumidifierOnRateText, JTextField HumidifierOffRateText, 
			JLabel CurrentHumidityLabel, JPanel HumidifierIndicator) {
		this.IdealHumidityText = IdealHumidityText;
		this.IdealHumidityRangeText = IdealHumidityRangeText;
		this.HumidifierOnRateText = HumidifierOnRateText;
		this.HumidifierOffRateText = HumidifierOffRateText;
		this.CurrentHumidityLabel = CurrentHumidityLabel;
		this.HumidifierIndicator = HumidifierIndicator;
		this.humidifierMode = HumidifierMode.OFF;
		this.SimulationData = new StringBuilder();
	}

	public void setCurrentHumidity(int CurrentHumidity) {
		this.CurrentHumidity = CurrentHumidity;
	}

	public void pauseSimulation(boolean pause) {
		pauseSimulation = pause;
	}

	public void run() {

		while (running) {
			if (!pauseSimulation) {
				int IdealHumidity = Integer.parseInt(IdealHumidityText.getText());
				int IdealHumidityRange = Integer.parseInt(IdealHumidityRangeText.getText());
				int HumidifierOnRate = Integer.parseInt(HumidifierOnRateText.getText());
				int HumidifierOffRate = Integer.parseInt(HumidifierOffRateText.getText());

				if (CurrentHumidity < IdealHumidity - IdealHumidityRange) {
					humidifierMode = HumidifierMode.ON;
					CurrentHumidity += HumidifierOnRate;
				} else if (CurrentHumidity > IdealHumidity + IdealHumidityRange) {
					humidifierMode = HumidifierMode.OFF;
					CurrentHumidity -= HumidifierOffRate;
				} else {
					humidifierMode = HumidifierMode.OFF;
					CurrentHumidity -= HumidifierOffRate;
				}

				if (CurrentHumidity > 100) {
					CurrentHumidity = 100;
				} else if (CurrentHumidity < 0) {
					CurrentHumidity = 0;
				}

				CurrentHumidityLabel.setText("Current Humidity: " + CurrentHumidity);
				HumidifierIndicator.setBackground(humidifierMode == HumidifierMode.ON ? Color.GREEN : Color.RED);

				if (humidifierMode == HumidifierMode.OFF) {
                    HumidifierOffRateText.setBackground(Color.GREEN);
                } else {
                	HumidifierOffRateText.setBackground(Color.WHITE);
                }

				SimulationData.append("Humidity: ").append(CurrentHumidity)
                .append(", Humidifier Indicator: ").append(humidifierMode == HumidifierMode.ON ? "ON" : "OFF")
                .append('\n');

				try {
					Thread.sleep(3000);
				} catch (InterruptedException e) {
					break;
				}
			}
		}
	}

	public void stopThread() {
		running = false;
	}

	public int getCurrentHumidity() {
		return CurrentHumidity;
	}

	private enum HumidifierMode {
		ON, OFF
	}


}

#include <vector>
#include <cmath>
#include <iostream>

class Polynomial {
    public:
        Polynomial(const std::vector<double>& coefficients) : coefficients(coefficients) { }

        double operator()(double x) const {
            double result = 0;
            for (int i = 0; i < coefficients.size(); i++) {
                result += coefficients[i] * std::pow(x, i);
            }
            return result;
        }

        Polynomial operator+(const Polynomial& other) const {
            int size = std::max(coefficients.size(), other.coefficients.size());
            std::vector<double> result(size, 0);
            for (int i = 0; i < size; i++) {
                if (i < coefficients.size()) {
                    result[i] += coefficients[i];
                }
                if (i < other.coefficients.size()) {
                    result[i] += other.coefficients[i];
                }
            }
            return Polynomial(result);
        }

        Polynomial operator-(const Polynomial& other) const {
            int size = std::max(coefficients.size(), other.coefficients.size());
            std::vector<double> result(size, 0);
            for (int i = 0; i < size; i++) {
                if (i < coefficients.size()) {
                    result[i] += coefficients[i];
                }
                if (i < other.coefficients.size()) {
                    result[i] -= other.coefficients[i];
                }
            }
            return Polynomial(result);
        }

        Polynomial operator*(const Polynomial& other) const {
            int size = coefficients.size() + other.coefficients.size() - 1;
            std::vector<double> result(size, 0);
            for (int i = 0; i < coefficients.size(); i++) {
                for (int j = 0; j < other.coefficients.size(); j++) {
                    result[i + j] += coefficients[i] * other.coefficients[j];
                }
            }
            return Polynomial(result);
        }

